import React from 'react';

class Forecast extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        data: [],
        location: [],
        current: [],
        forecast: [],
      };
    }

    componentDidMount() {
      fetch("http://api.weatherapi.com/v1/forecast.json?key=a60212c2ecf94da9a2101045212705&q=high Point&days=7&aqi=no&alerts=no")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              data: result,
              location: result.location,
              current: result.current,
              forecast: result.forecast
            });
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    getDay() {
        var days = {1: 'Monday', 2: 'Tuesday', 3: 'Wednesday', 4: 'Thursday', 5: 'Friday', 6: 'Saturday', 0: 'Sunday'};
        var d = new Date();
        return days[d.getDay()];
    }

    getDate() {
        var d = new Date();
        return d.getDate();
    }

    getMonth() {
        var months = ['Jan', 'Feb', 'March', 'April', 'May']
        var month = new Date();
        return month.getMonth();
    }

    render() {
      const { error, isLoaded, data, location, current, forecast } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
            <div class="forecast-table">
                <div class="container">
					<div class="forecast-container">
						<div class="today forecast">
							<div class="forecast-header">
								<div class="day" id="day">{this.getDay()}</div>
								<div class="date">{this.getDate()} Oct</div>
							</div> 
							<div class="forecast-content">
                                <div class="location">{location.name}</div>
								<div class="degree">
									<div class="num">{current.temp_f}<sup>o</sup>F</div>
									<div class="forecast-icon">
										<img src={current.icon} alt="" />
									</div>	
								</div>
								<span><img src="images/icon-umberella.png" alt="" />20%</span>
								<span><img src="images/icon-wind.png" alt="" />18km/h</span>
								<span><img src="images/icon-compass.png" alt="" />East</span>
							</div>
						</div>
						<div class="forecast">
							<div class="forecast-header">
								<div class="day">Tuesday</div>
							</div> 
							<div class="forecast-content">
								<div class="forecast-icon">
									<img src="images/icons/icon-3.svg" alt="" width="48" /> 
								</div>
								<div class="degree">23<sup>o</sup>C</div>
								<small>18<sup>o</sup></small>
							</div>
						</div>
						<div class="forecast">
							<div class="forecast-header">
								<div class="day">Wednesday</div>
							</div> 
							<div class="forecast-content">
								<div class="forecast-icon">
									<img src="images/icons/icon-5.svg" alt="" width="48" />
								</div>
								<div class="degree">23<sup>o</sup>C</div>
								<small>18<sup>o</sup></small>
							</div>
						</div>
						<div class="forecast">
							<div class="forecast-header">
								<div class="day">Thursday</div>
							</div> 
							<div class="forecast-content">
								<div class="forecast-icon">
									<img src="images/icons/icon-7.svg" alt="" width="48" />
								</div>
								<div class="degree">23<sup>o</sup>C</div>
								<small>18<sup>o</sup></small>
							</div>
						</div>
						<div class="forecast">
							<div class="forecast-header">
								<div class="day">Friday</div>
							</div> 
							<div class="forecast-content">
								<div class="forecast-icon">
									<img src="images/icons/icon-12.svg" alt="" width="48" />
								</div>
								<div class="degree">23<sup>o</sup>C</div>
