import React from 'react';
import ReactDOM from 'react-dom';   

class Forecast extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        data: [],
        location: [],
        current: [],
      };
    }

    componentDidMount() {
      fetch("http://api.weatherapi.com/v1/forecast.json?key=a60212c2ecf94da9a2101045212705&q=London&days=7&aqi=no&alerts=no")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              data: result,
              location: result.location,
              current: result.current
            });
          },
          // Note: it's important to handle errors here
          // instead of a catch() block so that we don't swallow
          // exceptions from actual bugs in components.
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, data, location, current } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
          <ul>
            {JSON.stringify(data.forecast)}<br/>
            {JSON.stringify(location)}<br/>
            {JSON.stringify(current)}
          </ul>
        );
      }
    }
  }

  export default Forecast;
